# Krasser .NET App Installer ??Write-Host "###########################################"Write-Host "#   gogogo.exe INSTALLATION - LET'S GO!   #"Write-Host "###########################################"Write-Host ""# Automatisch den aktuellen Ordner als Quelle nutzen$source = $PSScriptRootWrite-Host "Installationsquelle: $source"Write-Host "Soll die Installation für alle Benutzer oder nur für dich ($env:USERNAME) sein?"Write-Host "1 - Nur für mich (Current User)"Write-Host "2 - Für ALLE Benutzer (Admin nötig)"Write-Host "3 - Abbrechen"$choice = Read-Host "Wähle eine Option (1, 2 oder 3)"switch ($choice) {    "1" {         $installDir = Join-Path -Path $env:LOCALAPPDATA -ChildPath "gogogo"        $desktopPath = [Environment]::GetFolderPath("Desktop")    }    "2" {        if (-not ([Security.Principal.WindowsPrincipal][Security.Principal.WindowsIdentity]::GetCurrent()).IsInRole([Security.Principal.WindowsBuiltInRole]::Administrator)) {            Write-Host "ERROR: Brauch Admin-Rechte!"            Write-Host "Rechtsklick -> 'Als Admin ausführen'!"            Read-Host "Drücke Enter, um fortzufahren..."            exit        }        $installDir = Join-Path -Path $env:ProgramData -ChildPath "gogogo"        $desktopPath = [Environment]::GetFolderPath("CommonDesktopDirectory")    }    "3" {         Write-Host "Abbruch... Vielleicht später!"        Read-Host "Drücke Enter, um fortzufahren..."        exit     }    default {         Write-Host "Haha, falsche Eingabe! Nur 1-3!"        exit     }}# Zielordner erstellenif (-not (Test-Path -Path $installDir)) {    New-Item -Path $installDir -ItemType Directory}Write-Host "Kopiere Dateien..."Copy-Item -Path (Join-Path -Path $source -ChildPath "*") -Destination $installDir -Recurse -Force# Verknüpfung auf Desktop erstellen$shortcutPath = Join-Path -Path $desktopPath -ChildPath "gogogo.lnk"$targetPath = Join-Path -Path $installDir -ChildPath "gogogo.exe"$wscript = New-Object -ComObject WScript.Shell$shortcut = $wscript.CreateShortcut($shortcutPath)$shortcut.TargetPath = $targetPath$shortcut.WorkingDirectory = $installDir$shortcut.Save()Write-Host "FERTIG! Installation abgeschlossen!"Write-Host "Exe: $targetPath"Write-Host "Verknüpfung: $shortcutPath"Read-Host "Drücke Enter, um fortzufahren..."